import os
from openai import OpenAI
token = os.environ["GITHUB_TOKEN"]
endpoint = "https://models.inference.ai.azure.com"
model_name = "gpt-4o"

client = OpenAI(
    base_url=endpoint,
    api_key=token,
)

response = client.chat.completions.create(
    messages=[
        {
            "role": "system",
            "content": "You are a helpful assistant.",
        },
        {
            "role": "user",
            "content": "你是一名图像处理工程师，需要计划滑块的横向移动范围。滑块图片是2张图像的base64值，你找出横向移动的像素值，只需要回答像素值即可?.如下json中的slidingImage与originalImage一个表示滑块，一个表示背景图" +"""{"slidingImage": "",
        "originalImage": ""}""",
        }
    ],
    model=model_name,
    temperature=1.,
    max_tokens=4096,
    top_p=1.
)

print(response.choices[0].message.content)